mapper.select:
SELECT EMP_NO, DEP_NO, EMP_RANK, EMP_POSITION, EMP_NAME, ENTRY_DATE, EMAIL
FROM HR
WHERE EMP_NO = #{empNo}

SELECT EMP_NO, EMP_NAME, EMP_RANK, EMP_POSITION
FROM HR
WHERE DEP_NO = #{depNo}

mapper.update:
UPDATE DEPARTMENT
SET DEP_NAME = #{depName}
WHERE DEP_NO = #{depNo}

UPDATE POST
SET TITLE = #{title},
    CONTENT = #{content}
WHERE POST_NO = #{postNo}

UPDATE FILE_INFO
SET FLAG_DEL = 'Y'
WHERE SEQ = #{fileNo}
