mapper.select:
SELECT EMP_NO, DEP_NO, EMP_RANK, EMP_POSITION, EMP_NAME, ENTRY_DATE, EMAIL, ADMIN_LEVEL
FROM HR
WHERE EMP_NO = #{empNo}

SELECT EMP_NO, EMP_NAME, EMP_RANK, EMP_POSITION
FROM HR
WHERE DEP_NO = #{depNo}

mapper.update:
UPDATE DEPARTMENT
SET DEP_NAME = #{depName}
WHERE DEP_NO = #{depNo}

UPDATE POST
SET TITLE = #{title},
    CONTENT = #{content}
WHERE POST_NO = #{postNo}

UPDATE FILE_INFO
SET FLAG_DEL = 'Y'
WHERE SEQ = #{fileNo}

UPDATE ALARM
SET
    MESSAGE = #{message},
    START_TIME = #{startTime},
    END_TIME = #{endTime}
WHERE SEQ = #{seq}

UPDATE HR
SET LAST_LOGIN = SYSDATE
WHERE EMP_NO = #{empNo}

UPDATE HR
SET ADMIN_LEVEL = 1
WHERE EMP_NO = #{empNo}

UPDATE BOARD
SET NAME = #{depName}
WHERE DEP_NO = #{depNo}
  AND REF = 0

mapper.select:
SELECT B.SEQ, B.NAME
FROM BOARD B
WHERE SEQ = #{seq}

SELECT EMP_NO
FROM BOARD_USER
WHERE BOARD_SEQ = #{seq}

mapper.delete:
DELETE BOARD_USER
WHERE BOARD_SEQ = #{seq}
AND EMP_NO IN

        #{a}

mapper.update:
UPDATE BOARD
SET NAME = #{name}
WHERE SEQ = #{seq}
