mapper.select:
SELECT * FROM (
SELECT ROWNUM RN, T.* FROM (
SELECT P.*, H.EMP_NAME, D.DEP_NAME FROM POST P INNER JOIN DEPARTMENT D ON P.BOARD_NO = D.DEP_NO INNER JOIN HR H ON P.EMP_NO = H.EMP_NO WHERE P.BOARD_NO = #{boardNo} AND P.IS_DELETED = 'N'
AND P.CATEGORY = #{category}
AND P.TITLE LIKE '%' || #{search} || '%'
ORDER BY P.CREATED_TIME DESC
) T ) WHERE RN BETWEEN #{startRow} AND #{endRow}

mapper.update:
UPDATE POST
SET
	TITLE = #{title},
	CATEGORY = #{category},
	CONTENT = #{content}
WHERE POST_NO = #{postNo}

UPDATE REPLY
SET
	CONTENT = #{content}
WHERE REPLY_NO = #{replyNo}

UPDATE POST
SET VIEW_CNT = VIEW_CNT + 1
WHERE POST_NO= #{no}
